import org.apache.commons.codec.DecoderException;
import org.apache.commons.codec.binary.Base64;
import org.apache.commons.codec.binary.Hex;

import javax.crypto.Cipher;
import javax.crypto.spec.SecretKeySpec;
import java.nio.charset.StandardCharsets;

public class Decoder {

    private static final String aesKey = "L14WM0FLIN+aysbTiglo7g==";

    public static void main(String[] args) {
        String content = "";
        System.out.println(decode(content, aesKey, CodecType.HEX, CodecType.BASE64));
        String body = "";
    }

    public static String decode(String encryptContent, String key, CodecType encodeMode, CodecType keyEncodeMode) {
        byte[] keyBytes = str2Byte(key, keyEncodeMode);
        byte[] encryptDataBytes = str2Byte(encryptContent, encodeMode);
        return decode(encryptDataBytes, keyBytes);
    }

    public static String decode(byte[] encryptDataBytes, byte[] keyBytes) {
        try {
            SecretKeySpec skeySpec = new SecretKeySpec(keyBytes, "AES");
            Cipher cipher = Cipher.getInstance("AES");
            cipher.init(Cipher.DECRYPT_MODE, skeySpec);
            byte[] original = cipher.doFinal(encryptDataBytes);
            return new String(original, StandardCharsets.UTF_8);
        } catch (Exception ignored) {
            return null;
        }
    }

    public static byte[] str2Byte(String key, CodecType encodeMode) {
        byte[] bytes;
        switch (encodeMode) {
            case HEX:
                bytes = decodeHex(key);
                break;
            case BASE64:
                bytes = Base64.decodeBase64(key);
                break;
            default:
                bytes = key.getBytes(StandardCharsets.UTF_8);
        }
        return bytes;
    }

    public static byte[] decodeHex(String data) {
        try {
            return Hex.decodeHex(data);
        } catch (DecoderException e) {
            return null;
        }
    }

    public enum CodecType {
        BASE64,
        HEX,
        BYTE
    }
}
