    public int numMatchingSubseq(String S, String[] words) {
        int result = 0;
        for (String word : words) {
            if (isMatchingSubseq(S, word)) {
                result++;
            }
        }
        return result;
    }

    private boolean isMatchingSubseq(String S, String word) {
        if ("".equals(word)) {
            return true;
        }
        if ("".equals(S)) {
            return false;
        }
        int wordIndex = -1;
        int index = -1;
        while (wordIndex < word.length() - 1) {
            wordIndex++;
            index = S.indexOf(word.charAt(wordIndex), index + 1);
            if (index == -1) {
                return false;
            }
        }

        return true;
    }
